if (!h2a) {
  function h2a(hex) {
    const res = new Uint8Array(hex.length / 2);
    for (let i = 0; i < hex.length; i = i + 2) {
        const c = hex.slice(i, i + 2);
        res[i / 2] = parseInt(c, 16);
    }
    return res.buffer;
  }  
}
